import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { userUpdateProfile } from "../../redux/UserSlice";
import UserService from "../../services/UserService";
import "./update.css"; // Import the CSS file

const UpdateProfile = () => {
    const dispatch = useDispatch();
    const userData = useSelector((state) => state.user.loggedInUser);
    const token = useSelector(store => store.user.jwtToken);
    const [formData, setFormData] = useState(userData);

    const handleChange = (evt) => {
        setFormData({
            ...formData,
            [evt.target.name]: evt.target.value
        });
    };

    const handleSubmit = async (evt) => {
        evt.preventDefault();
        try {
            const user = await UserService.updateUserProfile(formData, token);
            dispatch(userUpdateProfile(user));
        } catch (error) {
            if (error.code === 'ERR_BAD_REQUEST') {
                alert(error.message);
            }
        }
    };

    return (
        <div className="update-profile-container">
            <h1>Update Your Profile</h1>
            <form onSubmit={handleSubmit}>
                <label>First Name:</label>
                <input type="text" name="firstName" value={formData.firstName} onChange={handleChange} autoFocus required />
                <label>Last Name:</label>
                <input type="text" name="lastName" value={formData.lastName} onChange={handleChange} required />
                <label>Phone:</label>
                <input type="number" name="phone" value={formData.phone} onChange={handleChange} required />
                <label>Email:</label>
                <input type="email" name="email" value={formData.email} onChange={handleChange} required />
                <label>Avatar:</label>
                <input type="text" name="avatar" value={formData.avatar} onChange={handleChange} />
                <button type="submit">Update Your Profile</button>
            </form>
        </div>
    );
};

export default UpdateProfile;
