import axios from "axios";
import { useState } from "react";

const DeleteEmployee = () => {
    const [employeeId, setEmployeeId] = useState('');
    const [message, setMessage] = useState('');

    const handleInputChange = (event) => {
        setEmployeeId(event.target.value);
    };

    const deleteEmployee = () => {
        axios.delete(`http://localhost:9090/emp/delete-emp/${employeeId}`)
            // .then((resp) => {
            //     console.log(resp); // Check the value of resp
            //     setMessage(resp.data.message);
            // })
            // .catch((error) => {
            //     console.error(error); // Check the error object
            //     setMessage(`Error deleting employee: ${error.response.data.message}`);
            // });
    };
    

   
    return (
        <div className="container">
            <h2>Delete Employee</h2>
            <div className="mb-3">
                <label htmlFor="employeeId" className="form-label">Employee ID:</label>
                <input
                    type="text"
                    id="employeeId"
                    name="employeeId"
                    value={employeeId}
                    onChange={handleInputChange}
                    className="form-control"
                />
            </div>
            <button onClick={deleteEmployee} className="btn btn-danger">Delete Employee</button>
            <p className="mt-3">{message}</p>
        </div>
    );
};



export default DeleteEmployee;
