import React, { useState } from "react";
import { Link } from "react-router-dom";

const Login = () => {
    const [loginData, setLoginData] = useState({ username: "", password: "" });
    const [afterSubmit, setAfterSubmit] = useState("");

    const handleChange = (evt) => {
        const { name, value } = evt.target;
        setLoginData({
            ...loginData,
            [name]: value
        });
    };

    const handleLoginSubmit = (evt) => {
        evt.preventDefault();
        // Your login logic here
        setAfterSubmit("Login successful!"); // Placeholder for actual logic
    };

    return (
        <div className="container mt-5">
            <h1>Login Component</h1>
            <p>Login here</p>
            <form onSubmit={handleLoginSubmit}>
                <div className="mb-3">
                    <label htmlFor="username" className="form-label">Username:</label>
                    <input type="text" id="username" name="username" value={loginData.username}
                        onChange={handleChange} className="form-control" required />
                </div>
                <div className="mb-3">
                    <label htmlFor="password" className="form-label">Password:</label>
                    <input type="password" id="password" name="password" value={loginData.password}
                        onChange={handleChange} className="form-control" required />
                </div>
                <button type="submit" className="btn btn-primary">Login</button>
            </form>
            {afterSubmit && <p className="success-message">{afterSubmit}</p>}
            <p>Not yet registered? <Link to={'/register'}>Register</Link></p>
        </div>
    );
};

export default Login;
