import { useState } from "react";
import EmployeeService from "../../services/EmployeeService";
import validationSchema from "../validations/validationSchema";

const DeleteEmployee = () => {
    const [employeeId, setEmployeeId] = useState('');
    const [message, setMessage] = useState('');
    const [isValid, setIsValid] = useState(false);

    const handleInputChange = async (event) => {
        setEmployeeId(event.target.value);
        try{
            await validationSchema.validate({ employeeId: event.target.value }, { abortEarly: false });
            setMessage('')
            setIsValid(true);
        }
        catch(err){
            console.log(err);
            setMessage(err.errors[0]); // Display only the first error
            setIsValid(false)
        }
    };
   
    // const BASE_URL="http://localhost:9090/emp/delete-emp";
    const deleteEmployee = async(employeeId) => {
        console.log(employeeId);
        console.log('hi');
        try {
           await EmployeeService.delete(employeeId);
            // console.log(user);
      
    
        }
        catch (error) {
            console.log(error);
            if (error.code === 'ERR_BAD_REQUEST')
                alert(error.message);
        }
       
    };



    return (
        <div className="container">
            <h2>Delete Employee</h2>
            <div className="mb-3">
                <label htmlFor="employeeId" className="form-label">Employee ID:</label>
                <input
                    type="text"
                    id="employeeId"
                    name="employeeId"
                    value={employeeId}
                    onChange={handleInputChange}
                    className="form-control"
                />
            </div>
            <button onClick={deleteEmployee} className="btn btn-danger">Delete Employee</button>
            <p className="mt-3">{message}</p>
        </div>
    );
};



export default DeleteEmployee;
