import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UpdateEmployee = ({ employeeId }) => {
    const [employee, setEmployee] = useState({
        firstName: '',
        lastName: '',
        email: '',
        salary: ''
    });
    

    useEffect(() => {
        // Fetch employee details when component mounts
        axios.get(`http://localhost:9090/update-emp/${employeeId}`)
            .then((response) => {
                setEmployee(response.data);
            })
            .catch((error) => {
                console.error('Error fetching employee details:', error);
            });
    }, [employeeId]);

    const handleChange = (event) => {
        const { name, value } = event.target;
        setEmployee(prevEmployee => ({
            ...prevEmployee,
            [name]: value
        }));
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        // Send updated employee details to the server
        axios.put(`http://localhost:9090/emp/update-emp/${employeeId}`, employee)
            .then((response) => {
                console.log('Employee details updated successfully:', response.data);
                // Optionally, redirect or display a success message
            })
            .catch((error) => {
                console.error('Error updating employee details:', error);
                // Handle error, e.g., display error message to the user
            });
    };

    return (
        <div>
            <h2>Update Employee Details</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>First Name:</label>
                    <input type="text" name="firstName" value={employee.firstName} onChange={handleChange} />
                </div>

                <div>
                    <label>Email:</label>
                    <input type="email" name="email" value={employee.email} onChange={handleChange} />
                </div>
                <div>
                    <label>Salary:</label>
                    <input type="number" name="salary" value={employee.salary} onChange={handleChange} />
                </div>
                <button type="submit">Update</button>
            </form>
        </div>
    );
};

export default UpdateEmployee;
