import React, { useState } from 'react';
import axios from 'axios';

function Search() {
    const [usename, setUseName] = useState('');

    const handleChange = (event) => {
        setUseName(event.target.value);
    }

    const handleSubmit = () => {
        axios.get(`http://localhost:9090/emp/get-emp-by-name/${usename}`)
        .then(resp => {
            console.log(resp);
            // Do something with the response data if needed
            setUseName(resp.data[0]);
        })
        .catch(err => console.log(err));
    }

    return (
        <div className="input-group">
            <div className="form-outline" data-mdb-input-init>
                <input type="text" value={usename} id="name" className="form-control" onChange={handleChange} />
                <label className="form-label" htmlFor="name">Search</label>
            </div>
            <button type="button" className="btn btn-primary" data-mdb-ripple-init onClick={handleSubmit}>
                <i className="fas fa-search"></i>
            </button>
        </div>
    );
}

export default Search;
